import React, { Component } from 'react';
import axios from 'axios';
import Navbar from "./Navbar";
import Profile from "./Profile";
import Repo from "./Repo";

class App extends Component {
  constructor(){
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.bdbea55c116f7d27",
        client_secret: "3946ce030300def37ac85aa6117b61563b499063",
      },
      user: [],
      repos: [],
    };
  }

  getUser = (e) => {
      const user = e.target.value;

      const {url, client_id, client_secret} = this.state.github;
        axios
          .get(
            `${url}/${user}?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ user: data }));
        axios
            .get(
              `${url}/${user}/repos?client_id=${client_id}&cliente_secret=${client_secret}`
            )
          .then(({ data }) => this.setState({ repos: data }));    
  };

  Profile = ({ user }) => (
    <div>
        <div className="row">
            <div className="col-md-4">
                <div className="card" style={{width: "18rem"}}>
                    <img className="card-img-top" alt="Profile" src={user.avatar_url}/>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            Repositórios: 
                            <span className="badge badge-primary">{user.public_repos}</span>
                        </li>
                        <li className="list-group-item">
                            Seguidores: 
                            <span className="badge badge-secondary">{user.followers}</span>
                        </li>
                    </ul>
                    <div className="card-body">
                        <button
                            type="button"
                            //onClick={() => showRepo = true)}
                            onClick={() => alert("BOTAO APERTADO")}
                            className="btn btn-primary btn-block">
                                Repositórios
                        </button>
                        <a href="user.html_url" className="btn btn-secondary btn-block">
                            Repositórios mais vistos
                        </a>
                    </div>
                </div>
            </div>
        </div>
    </div>
  );


  renderProfile = () => {
    const { user, repos } = this.state;

    return (
          <div className="row">
            <div className="col-md-4">
            <Profile user = {user} />
            </div>  
            <div className="col-md-8" id="repositories" style={{display: 'none'}}>
            {repos.map(repo => <Repo key={repo.name} repo={repo}/>)}
            </div>
          </div>
    )
  }

  render() {
    console.log(this.state.user);
    return (
      <div className="App">
        <Navbar />

        <div className="container">
          <div className="card card-body mb-3">
            <h1>Pesquisar Usuários</h1>
            <p className="lead">
              Digite o nome desejado
            </p>
            <input 
              onChange={this.getUser}
              id="search" 
              type="text" 
              className="form-control" 
              required
            />
          </div> 
          { this.state.user.length !==0 ? this.renderProfile() : null}
        </div>
      </div>
    );
  }
}

export default App;
