import React, { Component, useState } from 'react';
import axios from 'axios';
import Navbar from "./Navbar";
import Profile from "./Profile";
import Repo from "./Repo";
import Starred from "./Starred";

class App extends Component {
  constructor(){
    super();
    this.showRepos = {visible: 'none'};
    this.showStarred = 'none';
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.bdbea55c116f7d27",
        client_secret: "3946ce030300def37ac85aa6117b61563b499063",
      },
      user: [],
      repos: [],
      starreds: [],
    };
  }

  getUser = (e) => {
      const user = e.target.value;

      const {url, client_id, client_secret} = this.state.github;
        axios
          .get(
            `${url}/${user}?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ user: data }));
        axios
            .get(
              `${url}/${user}/repos?client_id=${client_id}&cliente_secret=${client_secret}`
            )
            .then(({ data }) => this.setState({ repos: data }));   
        axios
          .get(
            `${url}/${user}/starred?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ starreds: data }));     
  };

  toggleVisible(){
    alert(this.showRepos.visible)
    console.log(this.showRepos.visible)
    this.setState({visible: 'block'});
  }

  renderProfile = () => {
    const { user, repos, starreds } = this.state;

    return (
          <div className="row">
            <div className="col-md-4">
              <button
                type="button"
                onClick={() => changeText("block")}
                //onClick={this.toggleVisible.bind(this)}
                className="btn btn-primary btn-block">
                CLICA NISSO PARA ABRIR
              </button>
            </div>
            <div className="col-md-4">
            <Profile user = {user} />
            </div>
            <div className="col-md-8"  style={{display: this.buttonText}}>
            {repos.map(repo => <Repo key={repo.name} repo={repo}/>)}
            </div>
            <div className="col-md-8" style={{display: this.showStarred}}>
            {starreds.map(starred => <Starred key={starred.login} starred={starred}/>)}
            </div>
          </div>
    )
  }

  render() {
    //console.log(this.state.starred);
    //console.log(this.showRepos);
    return (
      <div className="App">
        <Navbar />

        <div className="container">
          <div className="card card-body mb-3">
            <h1>Pesquisar Usuários</h1>
            <p className="lead">
              Digite o nome desejado
            </p>
            <input 
              onChange={this.getUser}
              id="search" 
              type="text" 
              className="form-control" 
              required
            />
          </div> 
          { this.state.user.length !==0 ? this.renderProfile() : null}
        </div>
      </div>
    );
  }
}

const [buttonText, setButtonText] = useState("none");
const changeText = (text) => setButtonText(text);

export default App;
