import React, { Component } from 'react';
import axios from 'axios';
import Navbar from "./Navbar";

class App extends Component {
  constructor(){
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.c342681ee8bc9314",
        client_secret: "bb21b3fc21d833153d8b817bcef149b9b743e298",
        sort: "created: asc",
      },
      user: [],
      repos: []
    };
  }

  getUser = (e) => {
      const user = e.target.value;

      const {url, client_id, client_secret, count, sort} = this.state.github;
        axios
          .get(
            `${url}/${user}?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ user: data }));
  }

  render() {
    return (
      <div className="App">
        <Navbar />

        <div className="container">
          <div className="card card-body">
            <h1>Pesquisar Usu√°rios</h1>
            <p className="lead">
              Digite o nome desejado
            </p>
            <input onChange={this.getUser} id="search" type="text" className="form-control" required />
          </div> 
        </div>
      </div>
    );
  }
}

export default App;
