import React, { Component } from 'react';
import axios from 'axios';
import Navbar from "./Navbar";
import Profile from "./Profile";

class App extends Component {
  constructor(){
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.bdbea55c116f7d27",
        client_secret: "3946ce030300def37ac85aa6117b61563b499063",
      },
      user: [],
      repos: []
    };
  }

  getUser = (e) => {
      const user = e.target.value;

      const {url, client_id, client_secret} = this.state.github;
        axios
          .get(
            `${url}/${user}?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ user: data }));
  }

  render() {
    console.log(this.state.user);
    const { user } = this.state;
    return (
      <div className="App">
        <Navbar />

        <div className="container">
          <div className="card card-body mb-3">
            <h1>Pesquisar Usu√°rios</h1>
            <p className="lead">
              Digite o nome desejado
            </p>
            <input 
              onChange={this.getUser}
              id="search" 
              type="text" 
              className="form-control" 
              required
            />
          </div> 
          { user.length !==0 ? <Profile user = {user} /> : null}
        </div>
      </div>
    );
  }
}

export default App;
