import React, { Component } from 'react';
import axios from 'axios';
import Navbar from "./Navbar";
import Profile from "./Profile";
import Repo from "./Repo";

class App extends Component {
  constructor(){
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.bdbea55c116f7d27",
        client_secret: "3946ce030300def37ac85aa6117b61563b499063",
        count: 7,
        sort: "created: asc"
      },
      user: [],
      repos: []
    };
  }

  getUser = (e) => {
      const user = e.target.value;

      const {url, client_id, client_secret, count, sort} = this.state.github;
        axios
          .get(
            `${url}/${user}?client_id=${client_id}&cliente_secret=${client_secret}`
          )
          .then(({ data }) => this.setState({ user: data }));
        axios
            .get(
              `${url}/${user}/repos?per_page=${count}&sprt=${sort}&client_id=${client_id}&cliente_secret=${client_secret}`
            )
          .then(({ data }) => this.setState({ repos: data }));    
  };

  renderProfile = () => {
    const { user } = this.state;

    return (
      <div className="row">
        <div className="col-md-4">
        <Profile user = {user} />
        </div>    
      </div>
    )
  }

  render() {
    console.log(this.state.user);
    return (
      <div className="App">
        <Navbar />

        <div className="container">
          <div className="card card-body mb-3">
            <h1>Pesquisar Usu√°rios</h1>
            <p className="lead">
              Digite o nome desejado
            </p>
            <input 
              onChange={this.getUser}
              id="search" 
              type="text" 
              className="form-control" 
              required
            />
          </div> 
          { this.state.user.length !==0 ? this.renderProfile() : null}
        </div>
      </div>
    );
  }
}

const renderRepos = () => {
  alert("FUI CLICADO");
  const { repos } = this.state;

  return (
    <div className="row"> 
      <div className="col-md-8">
        {repos.map(repo => <Repo key={repo.name} repo={repo}/>)}
      </div>
    </div>
  )
};

export default App;
export {renderRepos};
